import 'package:flutter/material.dart';

void main() => runApp(const MyApp());

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Material App',
      debugShowCheckedModeBanner: false,
      home: Inicio(),
    );
  }
}

class Inicio extends StatelessWidget {
  const Inicio({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('App Login')),
      body: Cuerpo(),
    );
  }
}

class Cuerpo extends StatelessWidget {
  const Cuerpo({super.key});

  @override
  Widget build(BuildContext context) {
    return Container(
      decoration: const BoxDecoration(
        image: DecorationImage(
          image: NetworkImage(
            'data:image/jpeg;base64,',
          ),
          fit: BoxFit.cover,
        ),
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          titulo(context),
          CampoUser(context),
          CampoPass(context),
          SizedBox(height: 20.0),
          BotonEntrar(),
        ],
      ),
    );
  }
}

@override
Widget titulo(BuildContext context) {
  return Center(
    child: Text(
      "Login",
      style: TextStyle(
        fontSize: 24,
        fontWeight: FontWeight.bold,
        color: Colors.white,
      ),
    ),
  );
}

Widget CampoUser(BuildContext context) {
  return Container(
    padding: EdgeInsets.symmetric(horizontal: 35.0, vertical: 30.0),
    child: TextField(
      decoration: InputDecoration(
        hintText: "Ingresa tu Usuario:",
        fillColor: Colors.white,
        filled: true,
      ),
    ),
  );
}

Widget CampoPass(BuildContext context) {
  return Container(
    padding: EdgeInsets.symmetric(horizontal: 35.0, vertical: 1.0),
    child: TextField(
      decoration: InputDecoration(
        hintText: "Ingresa tu Contraseña:",
        fillColor: Colors.white,
        filled: true,
      ),
    ),
  );
}

Widget BotonEntrar() {
  return ElevatedButton(
    onPressed: () {},
    style: ElevatedButton.styleFrom(
      backgroundColor: Colors.blue, 
      foregroundColor: const Color.fromARGB(
        255,
        255,
        255,
        255,
      ), // Color del texto
      textStyle: const TextStyle(fontSize: 20),
      padding: const EdgeInsets.symmetric(horizontal: 30, vertical: 15),
    ),
    child: const Text("Entrar"),
  );
}
